-- Clear existing menu items for this tenant
DELETE FROM menu_items WHERE tenant_id = '63c69ee3-0167-4799-8986-09df2824ab93';

-- Insert all menu items

ON CONFLICT (tenant_id, name) DO UPDATE 
SET category = EXCLUDED.category,
    description = EXCLUDED.description,
    display_order = EXCLUDED.display_order,
    updated_at = NOW();

-- Verification
SELECT 'Menu items loaded:' as status, count(*) as count 
FROM menu_items 
WHERE tenant_id = '63c69ee3-0167-4799-8986-09df2824ab93';

-- Show categories
SELECT category, count(*) as items 
FROM menu_items 
WHERE tenant_id = '63c69ee3-0167-4799-8986-09df2824ab93'
GROUP BY category 
ORDER BY category;
